import java.util.ArrayList;import java.util.List;public class Student {    private int studentId;    private String name;    private List<Book> borrowedBooks;    public Student(int studentId, String name) {        this.studentId = studentId;        this.name = name;        this.borrowedBooks = new ArrayList<>();    }    public int getStudentId() {        return studentId;    }    public String getName() {        return name;    }    public List<Book> getBorrowedBooks() {        return borrowedBooks;    }    public void addBorrowedBook(Book book) {        borrowedBooks.add(book);    }    public void removeBorrowedBook(Book book) {        borrowedBooks.remove(book);    }}